import {reference} from './Firebase';
export const initialState = {
   users: [],
};

function reducer(state, action)
{

    switch(action.type)
    {
        case 'ADD_USER':
            var updates = {};
            var userData = {
                cc: action.user.cc,
                name: action.user.name,
                country: action.user.country,
            };
            updates['users/USR_'+ action.user.cc] =  userData;
           reference.update(updates);
            return {...state, users: [...state.users, action.user], };
        case 'UPDATE_USER':
            let newUsers = [...state.users];
            const index = state.users.findIndex((user) => user.cc === action.cc);
            if(index >= 0)
            {
                newUsers[index].cc = action.user.cc;
                newUsers[index].name = action.user.name;
                newUsers[index].country = action.user.country;
            } 
            return {...state, users: newUsers};
            case 'DELETE_USER':
                let newUsersToDelete = [...state.users];
                const deleteIndex = state.users.findIndex((user) => user.cc === action.cc);
                if(deleteIndex >= 0)
                {
                    newUsersToDelete.splice(deleteIndex, 1);
                } 
                return {...state, users: newUsersToDelete};
        default:
            return state;
    }
}

export default reducer;
